import "./App.css";
import axios from "axios";
import React, { useState, useEffect } from "react";
//import {Button, Badge } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
const URL = "http://localhost:3000";
function App() {
  const [data, setData] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [selectedUser, setSelectedUser] = useState(null); // State to store selected user
  const [selectedCountry, setSelectedCountry] = useState(null); // State to store selected user
  useEffect(() => {
    fetchUserData();
  }, []);
  useEffect(() => {
    fetchCountryData();
  }, []);
  const fetchUserData = async () => {
    try {
      const response = await axios.get(URL + "/user");
      console.log(response.data);
      setData(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const fetchCountryData = async () => {
    try {
      const response = await axios.get(URL + "/country");
      console.log(response.data);
      setData(response.data);
    } catch (error) {
      console.error(error);
    }
  };
    const handleChange = (e) => {
    setInputValue(e.target.value);
  };
  const handleUserSelect = (userId) => {
    const user = data.find((user) => user.id === userId);
    setSelectedUser(user);
  };
  
  const handleCountrySelect = (countryId) => {
    const country = data.find((country) => country.csem_id === countryId);
    setSelectedCountry(country);
  };

  const postData = async () => {
    try {
      const response = await axios.post(URL + "/user", { data: inputValue });
      console.log(response.data);
      fetchUserData(); // Fetch data again after posting
    } catch (error) {
      console.error(error);
    }
  };

  const dbinit = async () => {
    try {
      const response = await axios.post(URL + "/dbinit");
      console.log(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const tbinit = async () => {
    try {
      const response = await axios.post(URL + "/tbinit");
      console.log(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="App" class="p-3 mb-2 bg-dark text-dark">
<div class="container mt-5">
    <div class="row">
    <div classname="col-md-6 bg-light border-0 bg-dark text-white">
  <legend>
    <span classname="label label-default">
      <b>PRIVATE DETAILS</b>
    </span>
    <h6 classname="mt-1 mb-1">
      Note: This information will not be displayed in public.
    </h6>
  </legend>
  <div classname="col-md-3 col-sm-3 col-xs-3">&nbsp;</div>
  <div classname="col-md-3 col-sm-3 col-xs-3">&nbsp;</div>
  <form>
    <div classname="form-group row">
      <label htmlfor="exampleInputEmail1" classname="col-sm-6 col-form-label">
        Email
      </label>
      <div classname="col-sm-6">
        <input
          type="email"
          classname="email white col-7 col-md-4 col-lg-7 ml-3 form-control"
          id="exampleInputEmail1"
          aria-describedby="emailHelp"
          placeholder="Enter email"
        />
      </div>
    </div>
    <div classname="form-group row">
      <label
        htmlfor="exampleInputPassword1"
        classname="col-sm-6 col-form-label"
      >
        Password
      </label>
      <div classname="col-sm-6">
        <input
          type="password"
          classname="form-control"
          id="exampleInputPassword1"
          placeholder="Password"
        />
      </div>
    </div>
    <div classname="form-group row">
      <label htmlfor="exampleInputEmail1" classname="col-sm-6 col-form-labelt">
        Secure your account
      </label>
      <div classname="col-sm-6">
        <button type="button" classname="btn btn-secondary btn-sm active">
          Enable 2-Factor Authentication
        </button>
      </div>
    </div>
    <div classname="form-group row">
      <label htmlfor="exampleInputName" classname="col-sm-6 col-form-label">
        First Name
      </label>
      <div classname="col-sm-6">
        <input
          type="text"
          classname="form-control"
          id="exampleInputName"
          placeholder="First Name"
        />
      </div>
    </div>
    <div classname="form-group row">
      <label htmlfor="exampleInputLastName" classname="col-sm-6 col-form-label">
        Last Name
      </label>
      <div classname="col-sm-6">
        <input
          type="text"
          classname="form-control"
          id="exampleInputLastName"
          placeholder="Last Name"
        />
      </div>
    </div>
    <div classname="form-group row">
      <label htmlfor="exampleInputPhone" classname="col-sm-6 col-form-label">
        Phone
      </label>
      <div classname="col-sm-6">
        <input
          type="text"
          classname="form-control"
          id="exampleInputPhone"
          placeholder="Phone"
        />
      </div>
    </div>
    <div classname="form-group row">
      <label htmlfor="exampleInputPhone" classname="col-sm-6 col-form-label">
        Alternative Email for Alerts
      </label>
      <div classname="col-sm-6">
        <input
          type="text"
          classname="form-control"
          id="exampleInputPhone"
          placeholder="Phone"
        />
        <small>Email address that can be used to send alerts.</small>
      </div>
    </div>
    <div classname="col-md-3 col-sm-3 col-xs-3">&nbsp;</div>
    <div classname="col-md-3 col-sm-3 col-xs-3">&nbsp;</div>
    <button
      type="submit"
      classname="btn btn-secondary btn-sm active  float-end"
    >
      Save Changes
    </button>
  </form>
</div>
        <div class="col-md-6 bg-light border-0 bg-dark text-white">
            <legend>
            <span class="label label-default"><b>PRIVATE DETAILS</b></span>
              <h6  class="mt-1 mb-1">Note: This information will not be displayed in public.</h6> 
            </legend>
            <div class="col-md-3 col-sm-3 col-xs-3">&nbsp;</div>
            <div class="col-md-3 col-sm-3 col-xs-3">&nbsp;</div>
            <form id="form1" class="well well-lg">
            <div class="form-group row">
                    <label for="exampleInputName" class="col-sm-6 col-form-label">Name/ Nickname</label>
                    <div class="col-sm-6">
                    <input type="text" class="form-control" id="exampleInputName" placeholder="First Name"></input>
                </div>
                </div>

                <div class="form-group row">
                    <label for="exampleInputCountry" class="col-sm-6 col-form-label">Country</label>
                    <div class="col-sm-6"><select onChange={(e) => handleCountrySelect(parseInt(e.target.value))}>
        <option value="">Select a country</option>
        {data.map((country) => (
          <option key={country.csem_id} value={country.csem_id}>
            {country.csem_country_name}
          </option>
        ))}
      </select>

      {selectedCountry && (
        <div>
          <h3>Selected Country Details</h3>
          <p>ID: {selectedCountry.csem_id}</p>
          <p>Name: {selectedCountry.csem_country_name}</p>
          {/* Add more user details here */}
        </div>
      )}
               
                </div>
                </div>
                <div class="form-group row">
                    <label for="exampleInputProfilePic" class="col-sm-6 col-form-label">Profile Picture/ Avatar/ Logo</label>
                    <div class="col-sm-6">
                    <input type="file" class="form-control-file btn btn-secondary btn-sm active" id="exampleInputProfilePic"></input>
                   
                </div>
                <small id="profilePicHelp" class="form-text text-white   text-righ">JPG, GIF or PNG files supported, Max file size 700 KB</small>
                </div>
                <div class="col-md-3 col-sm-3 col-xs-3">&nbsp;</div><div class="col-md-3 col-sm-3 col-xs-3">&nbsp;</div><div class="col-md-3 col-sm-3 col-xs-3">&nbsp;</div>
                <button type="submit" class="btn btn-secondary btn-sm active  float-end">Save Changes</button>
            </form>
        </div>
    </div>
</div>

    <div>
    </div>
      <h1>User Submit Form</h1>
      <input name="input-parameter" onChange={handleChange} />
      <br />
      <br />
      <button onClick={postData}>Submit</button> <br />
       <hr />
      <h2>Users List</h2>
      <center>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
            </tr>
          </thead>
          <tbody>
            {data.map((user) => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.name}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </center>
    </div>
  );
}

export default App;
