import "./App.css";
import axios from "axios";
import React, { useState, useEffect } from "react";
//import {Button, Badge } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
const URL = "http://localhost:3000";
function App() {
  const [data, setData] = useState([]);
  const [inputValue, setInputValue] = useState("");

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get(URL + "/user");
      console.log(response.data);
      setData(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleChange = (e) => {
    setInputValue(e.target.value);
  };

  const postData = async () => {
    try {
      const response = await axios.post(URL + "/user", { data: inputValue });
      console.log(response.data);
      fetchData(); // Fetch data again after posting
    } catch (error) {
      console.error(error);
    }
  };

  const dbinit = async () => {
    try {
      const response = await axios.post(URL + "/dbinit");
      console.log(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const tbinit = async () => {
    try {
      const response = await axios.post(URL + "/tbinit");
      console.log(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="App" class="p-3 mb-2 bg-dark text-dark">





<div class="container mt-5">
    <div class="row">
        <div class="col-md-6 bg-light border-0 bg-dark text-white">
            <legend>
                <center>
                    <h2><b>PRIVATE DETAILS</b></h2>
                </center>
                <label class="fw-bold">Note: This information will not be displayed in public.</label> 
            </legend>
            <form>
                <div class="form-group row">
                    <label for="exampleInputEmail1" class="col-sm-4 col-form-label">Email</label>
                    <div class="col-sm-8">
                    <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email"></input>
                </div>
                </div>
                <div class="form-group row">
                    <label for="exampleInputPassword1" class="col-sm-4 col-form-label">Password</label>
                    <div class="col-sm-8">
                <input type="password" class="form-control" id="exampleInputPassword1" placeholder="Password"></input>
                </div>
                </div>
                <div class="form-group form-check row">
                  
                    <input type="checkbox" class="form-check-input" id="exampleCheck1"></input>
                    <label class="form-check-label" for="exampleCheck1">Check me out</label>
                </div>
                <button type="submit" class="btn btn-primary">Submit</button>
            </form>
        </div>
        <div class="col-md-6 bg-light border-0 bg-dark text-white">
            <legend>
                <center>
                    <h2><b>PUBLIC DETAILS</b></h2>
                </center>
            </legend>
            <form id="form1" class="well well-lg">
                <div class="form-group row">
                    <label for="exampleInputName" class="col-sm-4 col-form-label">First Name</label>
                    <div class="col-sm-8">
                    <input type="text" class="form-control" id="exampleInputName" placeholder="First Name"></input>
                </div>
                </div>
                <div class="form-group row">
                    <label for="exampleInputLastName" class="col-sm-4 col-form-label">Last Name</label>
                    <div class="col-sm-8">
                    <input type="text" class="form-control" id="exampleInputLastName" placeholder="Last Name"></input>
                </div>
                </div>
                <div class="form-group row">
                    <label for="exampleInputCountry" class="col-sm-4 col-form-label">Country</label>
                    <div class="col-sm-8">
                    <input type="text" class="form-control" id="exampleInputCountry" placeholder="Country"></input>
                </div>
                </div>
                <div class="form-group row">
                    <label for="exampleInputProfilePic" class="col-sm-4 col-form-label">Profile Picture/ Avatar/ Logo</label>
                    <div class="col-sm-8">
                    <input type="file" class="form-control-file" id="exampleInputProfilePic"></input>
                    <small id="profilePicHelp" class="form-text text-muted">JPG, GIF or PNG files supported, Max file size 700 KB</small>
                </div>
                </div>
                <button type="submit" class="btn btn-default">Submit</button>
            </form>
        </div>
    </div>
</div>

    <div>
    </div>
    <h1>User Submit Form</h1>
    <input name="input-parameter" onChange={handleChange} />
    <br />
    <br />
    <button onClick={postData}>Submit</button> <br />
      <br />
    <hr />
      <h1>User Submit Form</h1>
      <input name="input-parameter" onChange={handleChange} />
      <br />
      <br />
      <button onClick={postData}>Submit</button> <br />
      <br />
      <button style={{ backgroundColor: "red" }} onClick={dbinit}>
        DB Init
      </button>
      <br />
      <br />
      <button style={{ backgroundColor: "orange" }} onClick={tbinit}>
        Table Init
      </button>
      <br />
      <hr />
      <h2>Users List</h2>
      <center>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
            </tr>
          </thead>
          <tbody>
            {data.map((user) => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.name}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </center>
    </div>
  );
}

export default App;
