import "./App.css";
import axios from "axios";
import React, { useState, useEffect } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import Countries from "./Countries";
const URL = "http://localhost:3000";

 
function App() {
  const [data, setData] = useState([]);
  const [inputValue, setInputValue] = useState("");
 // set state
 const [countries, setCountries] = useState([]);

  
  const handleChange = (e) => {
    setInputValue(e.target.value);
  };

   
  const [selectedUser, setSelectedUser] = useState(null); // ---1
  const [selectedCountry, setSelectedCountry] = useState(null);
  const [formDataPrivate, setFormDataPrivate] = useState({
    InputFirstName: "",
    InputLastName: "",
    InputPhone: "",
    InputAltEmail: ""
  });
  const [formDataPublic, setFormDataPublic] = useState({
    InputFirstName: "",
    InputLastName: "",
    InputPhone: "",
    InputAltEmail: ""
  });
  useEffect(() => {
    fetchUserDataPrivate();//---2
  }, []);
  useEffect(() => {
    fetchCountryData();
  }, []);
  const fetchUserDataPrivate = async () => {
    try {
      const response = await axios.get(URL + "/user");
      setData(response.data);
      // Assign fetched data to form fields
      if (response.data.length > 0) {
        const userData = response.data[0]; // Assuming only one user is fetched
        console.log(userData);
        setFormDataPrivate({
          InputUserId: userData.scsemppri_id,
          InputFirstName: userData.scsemppri_first_name,
          InputLastName: userData.scsemppri_last_name,
          InputPhone: userData.scsemppri_phone,
          InputAltEmail: userData.scsemppri_alt_email
        });
      }
    } catch (error) {
      console.error(error);
    }
  };
  const fetchCountryData = async () => {
    try {
      const response = await axios.get(URL + "/country");
      console.log(response.data);
      setData(response.data);
    } catch (error) {
      console.error(error);
    }
  };
  const handleUserSelect = (userId) => {
    const user = data.find((user) => user.id === userId);//---4
    setSelectedUser(user);
  };
  const handleCountrySelect = (countryId) => {
    const country = data.find((country) => country.csem_id === countryId);//---4
    setSelectedCountry(country);
  };
 
  const postUserDataPrivate = async (formDataPrivate) => {
    try {
   
      const response = await axios.post(URL + "/user", formDataPrivate);
      console.log('--------ok------------');
      console.log(response.data);
       fetchUserDataPrivate(); // Fetch data again after posting
    } catch (error) {
      console.error(error);
    }
  };
  app.use(cors({
    origin: ["http://localhost:3000"],
    methods: ["GET", "POST", "PUT", "DELETE"],
    credentials: true,
    preflightContinue: false

})) 
app.use(cookieParser());
app.use(express.json());
app.use(bodyParser.urlencoded({extended: true}))
app.use(express.static("public"));

const storage = multer.diskStorage({
    destination: (req, file, cb) => {
        cb(null, "./public/images")
    },
    filename: (req, file, cb) => {
        cb(null, file.fieldname + "_" + Date.now() + path.extname(file.originalname))
    }
})

const upload = multer({
    storage: storage,
}).array("files")

app.get("/userpublic", (req, res) => {
    const sqlMedia = "SELECT * FROM csem_profile_public"

    db.query(sqlMedia, (err, result) => {
        if(err) return res.json(err)
        return res.json(result)
    })
})

app.post('/userpublic', upload, (req, res) => {
    console.log('success')
    console.log(req.files);
     const image = req.files.filename;

    const sqlImage = "INSERT INTO csem_profile_public (image) VALUES (?)"
    db.query(sqlImage, [image], (err, result) => {
        if(err) {
            return res.json({message: "Error uploading image"})
        } else {
            return res.json({message: "Success"})
        }
    })
})
  return (
    <div className="App" class="p-3 mb-2 bg-dark text-dark">


<h2>User List</h2>
      <select onChange={(e) => handleUserSelect(parseInt(e.target.value))}>
        <option value="">Select a user</option>
        {data.map((user) => (
          <option key={user.scsemppri_id} value={user.scsemppri_id}>
            {user.scsemppri_first_name}
          </option>
        ))}
      </select>

      {selectedUser && (
        <div>
          <h3>Selected User Details</h3>
          <p>ID: {selectedUser.scsemppri_id}</p>
          <p>Name: {selectedUser.scsemppri_first_name}</p>
          {/* Add more user details here */}
        </div>
      )}
<div class="container mt-5">
    <div class="row">
    <div className="col-md-6 bg-light border-0 bg-dark text-white">
      <legend>
        <span className="label label-default"><b>PRIVATE DETAILS</b></span>
        <h6 className="mt-1 mb-1">Note: This information will not be displayed in public.</h6>
      </legend>
      <div className="col-md-3 col-sm-3 col-xs-3">&nbsp;</div><div className="col-md-3 col-sm-3 col-xs-3">&nbsp;</div>
      <form   id="formpublic" onSubmit={(e) => {
        e.preventDefault();   
        postUserDataPrivate(formDataPrivate);
      }}>
        <div className="form-group row form-control-sm">
         <label htmlFor="exampleInputEmail1" className="col-sm-6 col-form-label">Email</label>
          <div className="col-sm-6">
          <button type="button" className="btn btn-secondary btn-sm active">Change Email</button>
          </div>
          <div className="col-sm-3">
                  </div>
        </div>
        <div className="col-sm-3">
                  </div>
        <div className="form-group row form-control-sm">
          <label htmlFor="exampleInputPassword1" className="col-sm-6 col-form-label">Password</label>
          <div className="col-sm-6">
          <button type="button" className="btn btn-secondary btn-sm active">Change Password</button>
          </div>
        </div>
        <div className="col-sm-3"></div>
        <div className="form-group row form-control-sm">
          <label htmlFor="exampleInputEmail1" className="col-sm-6 col-form-labelt">Secure your account</label>
          <div className="col-sm-6">
            <button type="button" className="btn btn-secondary btn-sm active">Enable 2-Factor Authentication</button>
          </div>
        </div>
        <div className="form-group row form-control-sm">
          <label htmlFor="exampleInputName" className="col-sm-6 col-form-label">First Name</label>
          <div className="col-sm-6">
          <input type="text" className="form-control" id="InputFirstName" name="InputFirstName" value={formDataPrivate.InputFirstName} placeholder="First Name" onChange={(e) => setFormDataPrivate({ ...formDataPrivate, InputFirstName: e.target.value })} />
            </div>
        </div>
        <div className="col-sm-3"></div>
        <div className="form-group row form-control-sm">
          <label htmlFor="exampleInputLastName" className="col-sm-6 col-form-label">Last Name</label>
          <div className="col-sm-6">
              <input type="text" className="form-control" id="InputLastName" name="InputLastName" value={formDataPrivate.InputLastName} placeholder="Last Name" onChange={(e) => setFormDataPrivate({ ...formDataPrivate, InputLastName: e.target.value })} />
         
          </div>
        </div>
        <div className="col-sm-3"></div>
        <div className="form-group row form-control-sm">
          <label htmlFor="exampleInputPhone" className="col-sm-6 col-form-label">Phone</label>
          <div className="col-sm-6">
          <div className="form-group">
            <input type="text" className="form-control" id="InputPhone" name="InputPhone" value={formDataPrivate.InputPhone} placeholder="Phone" onChange={(e) => setFormDataPrivate({ ...formDataPrivate, InputPhone: e.target.value })} />
         
            <button type="button" class="btn btn-secondary btn-sm active  float-end">+ Add Phone Number</button>
          </div>
          </div>
        </div>
        <div className="col-sm-3"></div>
        <div className="form-group row form-control-sm">
          <label htmlFor="exampleInputPhone" className="col-sm-6 col-form-label">Alternative Email for Alerts</label>
          <div className="col-sm-6">
          <div className="form-group row new-meows">
            <input type="text" className="form-control" id="InputAltEmail" name="InputAltEmail" value={formDataPrivate.InputAltEmail} placeholder="Email" onChange={(e) => setFormDataPrivate({ ...formDataPrivate, InputAltEmail: e.target.value })} />
             <button type="button" class="btn btn-secondary btn-sm active  float-end">+ Add Email</button>
              <small>Email address that can be used to send alerts.</small>
            <input type="hidden"  id="InputUserId" name="InputUserId" value={formDataPrivate.InputUserId} onChange={(e) => setFormDataPrivate({ ...formDataPrivate, InputUserId: e.target.value })} />
          
             </div>
           </div>
        </div>
        <div className="col-sm-3"></div>
        <div className="col-md-3 col-sm-3 col-xs-3">&nbsp;</div><div className="col-md-3 col-sm-3 col-xs-3">&nbsp;</div>
        <button type="submit" className="btn btn-secondary btn-sm active  float-end">Save Changes</button>
      </form>
    </div>
        <div class="col-md-6 bg-light border-0 bg-dark text-white">
            <legend>
            <span class="label label-default"><b>PRIVATE DETAILS</b></span>
              <h6  class="mt-1 mb-1">Note: This information will be displayed in public and visible for all users.</h6> 
            </legend>
            <div class="col-md-3 col-sm-3 col-xs-3">&nbsp;</div>
            <div class="col-md-3 col-sm-3 col-xs-3">&nbsp;</div>
            <form id="formprivalte" class="well well-lg">
            <div class="form-group row form-control-sm">
                    <label for="exampleInputName" class="col-sm-6 col-form-label">Name/ Nickname</label>
                    <div class="col-sm-6">
                    <input type="text" className="form-control" id="InputNickName" name="InputNickName" value={formDataPublic.InputAltEmail} placeholder="Name/ Nickname" onChange={(e) => setFormDataPublic({ ...setFormDataPublic, InputAltEmail: e.target.value })} />
                </div>
                </div>
                <div className="col-sm-3"></div>
                <div class="form-group row form-control-sm">
                    <label for="exampleInputCountry" class="col-sm-6 col-form-label">Country</label>
                    <div class="col-sm-6">
        <select class="form-select mb-3"  name="InputCountryCode" id="InputCountryCode" value={formDataPublic.InputCountryCode} onChange={(e) => handleCountrySelect(parseInt(e.target.value))}>
        <option value="">Select a country</option>
        {data.map((country) => (
          <option key={country.csem_id} value={country.csem_id}>
            {country.csem_country_name}
          </option>
        ))}
      </select>

      {selectedCountry && (
        <div>
          <h3>Selected Country Details</h3>
          <p>ID: {selectedCountry.csem_id}</p>
          <p>Name: {selectedCountry.csem_country_name}</p>
          {/* Add more user details here */}
        </div>
      )}
               
                </div>
                </div>
                <div className="col-sm-3"></div>
                <div class="form-group row">
                    <label for="exampleInputProfilePic" class="col-sm-6 col-form-label">Profile Picture/ Avatar/ Logo</label>
                    <div class="col-sm-6">
                    <div style={{ padding: 15, backgroundColor: '#fff', marginTop: 15 }}>
  <h4>Upload file:</h4>
  <input type="file" name='photo' onChange={setimgfile} multiple />
  <button onClick={addUserData}>submit</button>
  <ul>
        {fileList.map((file, i) => (
          console.log(file),
          <li key={i}>
            <img src={"http://localhost:3000/images/"+file.image} />
          </li>
        ))}
      </ul>
</div>
                    <input type="file" class="form-control-file btn btn-secondary btn-sm active" id="exampleInputProfilePic"></input>
                   
                </div>
                <small id="profilePicHelp" class="form-text text-white   text-righ">JPG, GIF or PNG files supported, Max file size 700 KB</small>
                </div>
                <div className="col-sm-3"></div>
                <div class="col-md-3 col-sm-3 col-xs-3">&nbsp;</div><div class="col-md-3 col-sm-3 col-xs-3">&nbsp;</div><div class="col-md-3 col-sm-3 col-xs-3">&nbsp;</div>
                <button type="submit" class="btn btn-secondary btn-sm active  float-end">Save Changes</button>
            </form>
        </div>
    </div>
</div>      
    </div>

    
  );
}

export default App;
