import "./App.css";
import axios from "axios";
import React, { useState, useEffect } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import Countries from "./Countries";
import Upload from './local-upload/Upload'
import Upload_Profile_Public from './local-upload/Upload_Profile_Public'
import Upload_Profile_Private from './local-upload/Upload_Profile_Private'
const URL = "http://localhost:3000";

 
function App() {
  const [data, setData] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [countries, setCountries] = useState([]);  
  const handleChange = (e) => {
    setInputValue(e.target.value);
  };

  const [selectedUser, setSelectedUser] = useState(null); // ---1
 

  const handleUserSelect = (userId) => {
    const user = data.find((user) => user.id === userId);//---4
    setSelectedUser(user);
  };
 
 
  
  /*
  const formData = new FormData(e.target);
  const userData = {};
  console.log(formData);
  console.log(formData.get("InputLastName") );
  if (formData.get("InputFirstName") && formData.get("InputLastName") && formData.get("InputPhone") && formData.get("InputAltEmail")) {
    formData.forEach((value, key) => {
      userData[key] = value;
    });
    postUserData(userData);
  } else {
    console.error("All fields are required");
  }
  */
  return (
    <div className="App" class="p-3 mb-2 bg-dark text-dark">
<Upload />

<h2>User List</h2>
      <select onChange={(e) => handleUserSelect(parseInt(e.target.value))}>
        <option value="">Select a user</option>
        {data.map((user) => (
          <option key={user.scsemppri_id} value={user.scsemppri_id}>
            {user.scsemppri_first_name}
          </option>
        ))}
      </select>

      {selectedUser && (
        <div>
          <h3>Selected User Details</h3>
          <p>ID: {selectedUser.scsemppri_id}</p>
          <p>Name: {selectedUser.scsemppri_first_name}</p>
          {/* Add more user details here */}
        </div>
      )}
<div class="container mt-5">
    <div class="row">
    <Upload_Profile_Public />
    <Upload_Profile_Private />
    </div>
</div>      
    </div>

    
  );
}

export default App;
