  const formData = new FormData(e.target);
  const userData = {};
  console.log(formData);
  console.log(formData.get("InputLastName") );
  if (formData.get("InputFirstName") && formData.get("InputLastName") && formData.get("InputPhone") && formData.get("InputAltEmail")) {
    formData.forEach((value, key) => {
      userData[key] = value;
    });
    postUserData(userData);
  } else {
    console.error("All fields are required");
  }
  */
  return (
    <div className="App" class="p-3 mb-2 bg-dark text-dark">
<Upload />

<h2>User List</h2>
      <select onChange={(e) => handleUserSelect(parseInt(e.target.value))}>
        <option value="">Select a user</option>
        {data.map((user) => (
          <option key={user.scsemppri_id} value={user.scsemppri_id}>
            {user.scsemppri_first_name}
          </option>
        ))}
      </select>

      {selectedUser && (
        <div>
          <h3>Selected User Details</h3>
          <p>ID: {selectedUser.scsemppri_id}</p>
          <p>Name: {selectedUser.scsemppri_first_name}</p>
          {/* Add more user details here */}
        </div>
      )}
<div class="container mt-5">
    <div class="row">
    <Upload_Profile_Public />
    <Upload_Profile_Private />
    </div>
</div>      
    </div>

    
  );
}

export default App;
