import "./App.css";
import axios from "axios";
import React, { useState, useEffect } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
const URL = "http://localhost:3000";
function App() {
  const [data, setData] = useState([]);
  const [inputValue, setInputValue] = useState("");
 // const [selectedUser, setSelectedUser] = useState(null); // State to store selected user
 // const [selectedCountry, setSelectedCountry] = useState(null); // State to store selected country
 // const [selectedPrivatedtl, setSelectedPrivatedtl] = useState(null); // State to store selected private details
  useEffect(() => {
    fetchUserData();
  }, []);
  useEffect(() => {
    fetchCountryData();
  }, []);
  const fetchUserData = async () => {
    try {
      const response = await axios.get(URL + "/user");
      console.log(response.data);
      setData(response.data);
    } catch (error) {
      console.error(error);
    }
  };
  const fetchCountryData = async () => {
    try {
      const response = await axios.get(URL + "/country");
      console.log(response.data);
      setData(response.data);
    } catch (error) {
      console.error(error);
    }
  };
  const handleChange = (e) => {
    setInputValue(e.target.value);
  };

  const postUserData = async () => {
    try {
      const response = await axios.post(URL + "/user", { data: inputValue });
      console.log(response.data);
      fetchUserData(); // Fetch data again after posting
    } catch (error) {
      console.error(error);
    }
  };
  const postCountryData = async () => {
    try {
      const response = await axios.post(URL + "/country", { data: inputValue });
      console.log(response.data);
      fetchCountryrData(); // Fetch data again after posting
    } catch (error) {
      console.error(error);
    }
  };
  const dbinit = async () => {
    try {
      const response = await axios.post(URL + "/dbinit");
      console.log(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const tbinit = async () => {
    try {
      const response = await axios.post(URL + "/tbinit");
      console.log(response.data);
    } catch (error) {
      console.error(error);
    }
  };



  
  return (
    <div className="App">
      <h1>User Submit Form</h1>
      <input name="input-parameter" onChange={handleChange} />
      <br />
      <br />
      <button onClick={postData}>Submit</button> <br />
      <br />
      <button style={{ backgroundColor: "red" }} onClick={dbinit}>
        DB Init
      </button>
      <br />
      <br />
      <button style={{ backgroundColor: "orange" }} onClick={tbinit}>
        Table Init
      </button>
      <br />
      <hr />
      <h2>Users List</h2>
      <center>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
            </tr>
          </thead>
          <tbody>
            {data.map((user) => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.name}</td>
              </tr>
            ))}
          </tbody>
          
        </table>
      </center>
    </div>
  );
}

export default App;
