import React, { useState, useEffect } from "react";
import axios from "axios";

const URL = "http://localhost:3000";

const Upload_Profile_Private = ({ initialUserData }) => {
  const [formData, setFormData] = useState({
    InputUserId: "",
    InputFirstName: "",
    InputLastName: "",
    InputPhone: "",
    InputAltEmail: ""
  });

  useEffect(() => {
    if (initialUserData.length > 0) {
      const userData = initialUserData[0]; // Assuming only one user is fetched
      setFormData({
        InputUserId: userData.scsemppri_id,
        InputFirstName: userData.scsemppri_first_name,
        InputLastName: userData.scsemppri_last_name,
        InputPhone: userData.scsemppri_phone,
        InputAltEmail: userData.scsemppri_alt_email
      });
    }
  }, [initialUserData]);

  const postUserData = async (formData) => {
    try {
      const response = await axios.post(URL + "/user", formData);
      console.log(response.data);
      // Fetch data again after posting if needed
    } catch (error) {
      console.error(error);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    postUserData(formData);
  };

  return (
    <div className="col-md-6 bg-light border-0 bg-dark text-white">
      <legend>
        <span className="label label-default"><b>PRIVATE DETAILS</b></span>
        <h6 className="mt-1 mb-1">Note: This information will not be displayed in public.</h6>
      </legend>
      <form onSubmit={handleSubmit}>
        <div className="form-group row">
          <label htmlFor="InputFirstName" className="col-sm-6 col-form-label">First Name</label>
          <div className="col-sm-6">
            <input type="text" className="form-control" id="InputFirstName" name="InputFirstName" value={formData.InputFirstName} onChange={handleChange} placeholder="First Name" />
          </div>
        </div>
        <div className="form-group row">
          <label htmlFor="InputLastName" className="col-sm-6 col-form-label">Last Name</label>
          <div className="col-sm-6">
            <input type="text" className="form-control" id="InputLastName" name="InputLastName" value={formData.InputLastName} onChange={handleChange} placeholder="Last Name" />
          </div>
        </div>
        <div className="form-group row">
          <label htmlFor="InputPhone" className="col-sm-6 col-form-label">Phone</label>
          <div className="col-sm-6">
            <input type="text" className="form-control" id="InputPhone" name="InputPhone" value={formData.InputPhone} onChange={handleChange} placeholder="Phone" />
          </div>
        </div>
        <div className="form-group row">
          <label htmlFor="InputAltEmail" className="col-sm-6 col-form-label">Alternative Email for Alerts</label>
          <div className="col-sm-6">
            <input type="text" className="form-control" id="InputAltEmail" name="InputAltEmail" value={formData.InputAltEmail} onChange={handleChange} placeholder="Email" />
          </div>
        </div>
        <button type="submit" className="btn btn-secondary btn-sm active float-end">Save Changes</button>
      </form>
    </div>
  );
};

export default Upload_Profile_Private;
