import "./App.css";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import React, { useState, useEffect } from "react";
import Upload_Profile_Private from "./Components/Upload_Profile_Private";
import Upload_Profile_Public from "./Components/Upload_Profile_Public";
import ReactNotification from "react-notifications-component";
import "react-notifications-component/dist/theme.css";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import vedeio1 from './assets/videos/204306-923909642_small.mp4';
import Login from './page/Login';
//import Register from './Page/Register';
//import PersonalInfo from './Page/PersonalInfo';
//import Profile from './Profile';
const URL = "http://localhost:3001";
function App() {
  const [data, setData] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [initialUserData, setInitialUserData] = useState([]);
  const [initialPublicUserData, setInitialPublicUserData] = useState([]);
  useEffect(() => {
    fetchData();
  }, []);
  useEffect(() => {
    fetchDataPublic();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get(URL + "/userGetPrivate");
      setInitialUserData(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const fetchDataPublic = async () => {
    try {
      const response = await axios.get(URL + "/userGetPublic");
      setInitialPublicUserData(response.data);
    } catch (error) {
      console.error(error);
    }
  };
  const handleChange = (e) => {
    setInputValue(e.target.value);
  };

  const postData = async () => {
    try {
      const response = await axios.post(URL + "/userPostPrivate", {
        data: inputValue,
      });
      console.log(response.data);
      fetchData(); // Fetch data again after posting
    } catch (error) {
      console.error(error);
    }
  };
  const postPublicData = async () => {
    try {
      const response = await axios.post(URL + "/userPostPublic", {
        data: inputValue,
      });
      console.log(response.data);
      fetchDataPublic(); // Fetch data again after posting
    } catch (error) {
      console.error(error);
    }
  };
  const [formData, setFormData] = useState({
    InputFirstName: "",
    InputLastName: "",
    InputPhone: "",
    InputAltEmail: "",
  });
  const [formDataUserPublic, setFormDataUserPublic] = useState({
    InputNickName: "",
    InputCountry: "",
  });
  const fetchUserData = async () => {
    try {
      const response = await axios.get(URL + "/userGetPrivate");
      setData(response.data);
      // Assign fetched data to form fields
      if (response.data.length > 0) {
        const userData = response.data[0]; // Assuming only one user is fetched
        console.log(userData);
        setFormData({
          InputUserId: userData.scsemppri_id,
          InputFirstName: userData.scsemppri_first_name,
          InputLastName: userData.scsemppri_last_name,
          InputPhone: userData.scsemppri_phone,
          InputAltEmail: userData.scsemppri_alt_email,
        });
      }
    } catch (error) {
      console.error(error);
    }
  };
  const fetchPublicUserData = async () => {
    try {
      const response = await axios.get(URL + "/userGetPublic");
      setData(response.data);
      // Assign fetched data to form fields
      if (response.data.length > 0) {
        const userDataPublic = response.data[0]; // Assuming only one user is fetched
        console.log(userDataPublic);
        setFormDataUserPublic({
          InputUserId: userDataPublic.scsemppri_id,
          InputNickName: userDataPublic.scsemppub_nickname,
          InputCountry: userDataPublic.scsemppub_countrycode,
        });
      }
    } catch (error) {
      console.error(error);
    }
  };
  const tbinit = async () => {
    try {
      const response = await axios.post(URL + "/tbinit");
      console.log(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
  <div className="App" class="px-4 py-5 px-md-5">
<section class="vh-100">
  <div class="container-fluid">
    <div class="row">
    <div class="col-sm-8 px-0 d-none d-sm-block">
    <video className='videoTag' autoPlay loop muted>
    <source src={vedeio1} type='video/mp4' />
</video>
      </div>
      <div class="col-sm-4 text-black">

        <div class="px-5 ms-xl-4">
          <i class="fas fa-crow fa-2x me-3 pt-5 mt-xl-4"></i>
          <span class="h1 fw-bold mb-0">Logo</span>
        </div>

        <div class="d-flex align-items-center h-custom-2 px-5 ms-xl-4 mt-5 pt-5 pt-xl-0 mt-xl-n5">

        <form onSubmit={LoginHandler} className='container mt-3 border' style={{width: '100%', padding: '20px', borderRadius: '10px', background: 'whitesmoke'}}>
        <div class="logo"><img class="" />img</div>
                    <h1 className='container d-flex justify-content-center'>Sign Up</h1>
                    <p id='err' className='d-flex justify-content-center' style={{color: 'red'}}></p>
                    <div className='mb-3 mt-3' dataValidate = "Username is reauired">
                        <label for='email' className='form-label'>Username:</label>
                        <div className='input-group d-flex align-items-center'>
                            <FontAwesomeIcon icon={faUser} />
                            <input 
                                type='email' 
                                className='form-control' 
                                id='username' 
                                placeholder='Enter email' 
                                name='username' 
                                onChange={(evt)=>setUserName(evt.target.value)} 
                                required
                            />
                        </div>
                    </div>
                    <div className='mb-3'>
                        <label htmlFor='password' className='form-label'>Password:</label>
                        <div className='input-group d-flex align-items-center'>
                            <FontAwesomeIcon icon={faLock} />
                            <input
                                type={showPassword ? 'text' : 'password'}
                                className='form-control'
                                id='password'
                                name='password'
                                placeholder='Enter your password'
                                onChange={(evt)=>setPassword(evt.target.value)}
                                required
                            />
                            <button
                                className='btn btn-outline-secondary'
                                type='button'
                                onClick={()=>setShowPassword(!showPassword)}
                            >
                                <FontAwesomeIcon icon={showPassword ? faEye : faEyeSlash} className='me-2' />
                            </button>
                        </div>
                    </div>
                    <div className='form-check mb-3 d-flex justify-content-end'>
                        <a href='/'>Forgot password?</a>
                    </div>
                    <div className='row' style={{marginBottom: '30px'}}>
                        <div className='col-sm-3'></div>
                        <div className='col-sm-1 d-flex align-items-center'>
                            <FontAwesomeIcon icon={faSyncAlt} onClick={()=>setCaptcha(Captcha())} style={{color: 'blue'}}/>
                        </div>
                        <div 
                            id="image"
                            className='col-sm-4 d-flex justify-content-center'
                            style={{
                                userSelect: 'none',
                                background: `url(${CaptchaBg})`,
                                border: '1px solid #ccc',
                                borderRadius: '5px',
                                padding: '10px',
                                backgroundSize: '100% ',
                                backgroundRepeat: 'no-repeat',
                                font: 'bold 20px/1 sans-serif',
                                textTransform: 'uppercase',
                                letterSpacing: '5px',
                                color: '#555',
                            }}
                            onClick={()=>setCaptcha(Captcha())}
                        >
                            {captcha}
                        </div>
                    </div>
                    <div className='d-flex justify-content-center mb-4' style={{marginBottom: '40px'}}>
                        <input 
                            type='text'
                            id='userCaptcha'
                            name='userCaptcha'
                            className='form-control text-center'
                            style={{ maxWidth: '300px', fontSize: '16px', fontWeight: 'normal', borderRadius: '10px' }}
                            placeholder='Type the characters you see'
                            maxLength={6}
                            onChange={(e)=>setUserChaptcha(e.target.value.toUpperCase())}
                        >
                        </input>
                    </div>
                    <div className='d-flex justify-content-center'>
                        <button type='submit' className='btn btn-primary rounded-pill' style={{width: '80%'}}>Login</button>
                    </div>
                    <div className='d-flex justify-content-center' style={{paddingTop: '40px'}}>
                        <p>or continue using</p>
                    </div>
                    <div className='d-flex justify-content-center'>
                        <a href='/' className='btn btn-danger'>
                            <FontAwesomeIcon icon={faGoogle}/>
                        </a>
                        <a href='/' className='btn btn-info' style={{ marginLeft: '20px' }}>
                            <FontAwesomeIcon icon={faYahoo} />
                        </a>
                    </div>
                    <div className='d-flex justify-content-center align-items-center' style={{paddingTop: '40px'}}>
                        <span style={{fontSize: '16px', marginRight: '10px'}}>Don't have an account? </span>
                        <a href='/register' style={{color: 'red', fontSize: '16px'}}>Register</a>
                    </div>
                </form> 

        </div>

      </div>

    </div>
  </div>
</section>
  </div> 
  );
}

export default App;
